        // it means that the contract already exist

        await currentBinaryContract.methods
          .addTransaction(
            this.state.playerOne,
            this.state.providedAmount,
            this.state.playerTwo
          )
          .send({
            from: accounts[0],
            gas: "2000000",
          });

        contractExisted = true;

